import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import { orderAPI } from '../services/api';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import './OrderDetails.css';

const OrderDetails = () => {
  const { id } = useParams();
  const [order, setOrder] = useState(null);
  const [loading, setLoading] = useState(true);
  const [deliveryPersonnel, setDeliveryPersonnel] = useState(null);

  useEffect(() => {
    loadOrder();
    // Refresh order data every 10 seconds for real-time updates
    const interval = setInterval(() => {
      loadOrder();
    }, 10000);

    return () => clearInterval(interval);
  }, [id]);

  const loadOrder = async () => {
    try {
      const response = await orderAPI.getById(id);
      const orderData = response.data;
      setOrder(orderData);
      
      // Load delivery personnel info if assigned
      if (orderData.deliveryPersonnel) {
        // In a real app, you'd have a separate API for delivery personnel
        // For demo, we'll simulate this data
        setDeliveryPersonnel({
          name: 'Loading...',
          phone: '+91 9999999999',
          vehicleType: 'Bike',
          rating: 4.8
        });
      }
    } catch (error) {
      console.error('Error loading order:', error);
    } finally {
      setLoading(false);
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'Pending': return '#ffc107';
      case 'Payment Processing': return '#17a2b8';
      case 'Paid': return '#007bff';
      case 'Preparing': return '#fd7e14';
      case 'Out for Delivery': return '#6f42c1';
      case 'Delivered': return '#28a745';
      case 'Cancelled': return '#dc3545';
      default: return '#6c757d';
    }
  };

  const getTrackingIcon = (status) => {
    switch (status) {
      case 'Payment Confirmed': return 'üí≥';
      case 'Preparing': return 'üë®‚Äçüç≥';
      case 'Delivery Assigned': return 'üöó';
      case 'On the way': return 'üõ£Ô∏è';
      case 'Nearby': return 'üìç';
      case 'Delivered': return '‚úÖ';
      default: return 'üì¶';
    }
  };

  const formatTime = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleTimeString('en-IN', { 
      hour: '2-digit', 
      minute: '2-digit',
      hour12: true 
    });
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('en-IN', {
      day: 'numeric',
      month: 'short',
      year: 'numeric'
    });
  };

  const getTimeRemaining = () => {
    if (!order?.estimatedDeliveryTime) return null;
    
    const now = new Date();
    const deliveryTime = new Date(order.estimatedDeliveryTime);
    const diff = deliveryTime - now;
    
    if (diff <= 0) return 'Delivered';
    
    const minutes = Math.floor(diff / (1000 * 60));
    const seconds = Math.floor((diff % (1000 * 60)) / 1000);
    
    return `${minutes}m ${seconds}s`;
  };

  if (loading) {
    return <div className="loading">Loading order details...</div>;
  }

  if (!order) {
    return (
      <div className="order-details-page">
        <div className="container">
          <div className="text-center">
            <h2>Order not found</h2>
            <Link to="/orders" className="btn btn-primary">Back to Orders</Link>
          </div>
        </div>
      </div>
    );
  }

  const timeRemaining = getTimeRemaining();

  return (
    <div className="order-details-page">
      <ToastContainer position="top-right" />
      <div className="container">
        <div className="order-header">
          <div className="order-title">
            <h1>Order #{order._id.slice(-8).toUpperCase()}</h1>
            <p>Placed on {formatDate(order.createdAt)} at {formatTime(order.createdAt)}</p>
          </div>
          <div className="order-status">
            <span 
              className="status-badge"
              style={{ backgroundColor: getStatusColor(order.status) }}
            >
              {order.status}
            </span>
            {timeRemaining && timeRemaining !== 'Delivered' && (
              <div className="delivery-timer">
                <span className="timer-label">ETA:</span>
                <span className="timer-value">{timeRemaining}</span>
              </div>
            )}
          </div>
        </div>

        {/* Live Tracking Section */}
        {order.deliveryTracking && order.deliveryTracking.length > 0 && (
          <div className="card tracking-card">
            <div className="card-header">
              <h3>üì± Live Order Tracking</h3>
            </div>
            <div className="card-body">
              <div className="tracking-timeline">
                {order.deliveryTracking.map((track, index) => (
                  <div key={index} className="tracking-item">
                    <div className="tracking-icon">
                      {getTrackingIcon(track.status)}
                    </div>
                    <div className="tracking-content">
                      <div className="tracking-status">{track.status}</div>
                      <div className="tracking-message">{track.message}</div>
                      <div className="tracking-time">
                        {formatTime(track.timestamp)}
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </div>
        )}

        {/* Delivery Personnel Info */}
        {order.deliveryPersonnel && (
          <div className="card delivery-card">
            <div className="card-header">
              <h3>üöó Your Delivery Partner</h3>
            </div>
            <div className="card-body">
              <div className="delivery-info">
                <div className="delivery-avatar">
                  <span className="avatar-icon">üë®‚Äçüíº</span>
                </div>
                <div className="delivery-details">
                  <h4>Ravi Kumar</h4>
                  <p>üì± +91 9876543210</p>
                  <p>üèçÔ∏è Bike Delivery</p>
                  <div className="delivery-rating">
                    <span>‚≠ê 4.8</span>
                    <span>(234 deliveries)</span>
                  </div>
                </div>
                <div className="delivery-actions">
                  <button className="btn btn-sm btn-outline-primary">
                    üìû Call
                  </button>
                  <button className="btn btn-sm btn-outline-secondary">
                    üí¨ Chat
                  </button>
                </div>
              </div>
            </div>
          </div>
        )}

        <div className="order-content">
          <div className="order-main">
            <div className="card">
              <div className="card-header">
                <h3>üì¶ Order Items</h3>
              </div>
              <div className="card-body">
                <div className="order-items">
                  {order.orderItems.map((item, index) => (
                    <div key={index} className="order-item">
                      <div className="item-info">
                        <h4>{item.name}</h4>
                        <p>Quantity: {item.quantity}</p>
                        <p>Price: ‚Çπ{item.price}</p>
                      </div>
                      <div className="item-total">
                        ‚Çπ{(item.price * item.quantity).toFixed(2)}
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            </div>

            <div className="card">
              <div className="card-header">
                <h3>üè† Delivery Address</h3>
              </div>
              <div className="card-body">
                <div className="address">
                  <p><strong>{order.shippingAddress.fullName}</strong></p>
                  <p>{order.shippingAddress.address}</p>
                  <p>
                    {order.shippingAddress.city}, {order.shippingAddress.postalCode}
                  </p>
                  <p>{order.shippingAddress.country}</p>
                </div>
              </div>
            </div>

            <div className="card">
              <div className="card-header">
                <h3>üí≥ Payment Information</h3>
              </div>
              <div className="card-body">
                <p><strong>Method:</strong> {order.paymentMethod}</p>
                <div className="payment-status">
                  <span className={`payment-badge ${order.isPaid ? 'paid' : 'unpaid'}`}>
                    {order.isPaid ? '‚úÖ Paid' : '‚è≥ Not Paid'}
                  </span>
                  {order.isPaid && order.paidAt && (
                    <span className="payment-date">
                      on {formatDate(order.paidAt)} at {formatTime(order.paidAt)}
                    </span>
                  )}
                </div>
              </div>
            </div>
          </div>

          <div className="order-sidebar">
            <div className="card">
              <div className="card-header">
                <h3>üí∞ Order Summary</h3>
              </div>
              <div className="card-body">
                <div className="summary-row">
                  <span>Items:</span>
                  <span>‚Çπ{order.itemsPrice.toFixed(2)}</span>
                </div>
                <div className="summary-row">
                  <span>Delivery:</span>
                  <span>‚Çπ{order.shippingPrice.toFixed(2)}</span>
                </div>
                <div className="summary-row">
                  <span>Tax (10%):</span>
                  <span>‚Çπ{order.taxPrice.toFixed(2)}</span>
                </div>
                <hr />
                <div className="summary-row total">
                  <span>Total Paid:</span>
                  <span>‚Çπ{order.totalPrice.toFixed(2)}</span>
                </div>
              </div>
            </div>

            {order.status === 'Delivered' && (
              <div className="card success-card">
                <div className="card-body text-center">
                  <div className="success-icon">üéâ</div>
                  <h4>Order Delivered!</h4>
                  <p>Thank you for choosing E-Shop!</p>
                  <p>Delivered on {formatDate(order.deliveredAt)}</p>
                </div>
              </div>
            )}

            <div className="order-actions">
              <Link to="/orders" className="btn btn-secondary">
                ‚Üê Back to Orders
              </Link>
              <Link to="/products" className="btn btn-primary">
                üõí Order Again
              </Link>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default OrderDetails;
