import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { productAPI } from '../services/api';
import ProductCard from '../components/ProductCard';
import './Home.css';

const Home = () => {
  const [featuredProducts, setFeaturedProducts] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const loadData = async () => {
      try {
        // Try to seed products first (ignore errors)
        try {
          await productAPI.seed();
        } catch (seedError) {
          console.log('Seed might already exist, continuing...');
        }

        // Get featured products with retry logic
        let retries = 3;
        while (retries > 0) {
          try {
            const response = await productAPI.getAll({ featured: 'true' });
            setFeaturedProducts(response.data);
            break;
          } catch (error) {
            retries--;
            if (retries === 0) throw error;
            await new Promise(resolve => setTimeout(resolve, 1000)); // Wait 1 second before retry
          }
        }
      } catch (error) {
        console.error('Error loading data:', error);
      } finally {
        setLoading(false);
      }
    };

    loadData();
  }, []);

  if (loading) {
    return <div className="loading">Loading...</div>;
  }

  return (
    <div className="home">
      <div className="container">
        <section className="hero">
          <div className="hero-content">
            <h1>Welcome to E-Shop</h1>
            <p>Discover amazing products at great prices</p>
            <Link to="/products" className="btn btn-primary hero-btn">
              Shop Now
            </Link>
          </div>
        </section>

        <section className="featured-products">
          <h2>Featured Products</h2>
          <div className="products-grid">
            {featuredProducts.map(product => (
              <ProductCard key={product._id} product={product} />
            ))}
          </div>
          {featuredProducts.length === 0 && (
            <p className="text-center">No featured products available.</p>
          )}
        </section>

        <section className="features">
          <div className="features-grid">
            <div className="feature">
              <div className="feature-icon">üöö</div>
              <h3>Free Shipping</h3>
              <p>Free shipping on orders over $50</p>
            </div>
            <div className="feature">
              <div className="feature-icon">üîí</div>
              <h3>Secure Payment</h3>
              <p>Your payment information is safe with us</p>
            </div>
            <div className="feature">
              <div className="feature-icon">‚Ü©Ô∏è</div>
              <h3>Easy Returns</h3>
              <p>30-day return policy for all products</p>
            </div>
          </div>
        </section>
      </div>
    </div>
  );
};

export default Home;
