import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useCart } from '../context/CartContext';
import { orderAPI, paymentAPI } from '../services/api';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import './Checkout.css';

const Checkout = () => {
  const { cartState, user, dispatch } = useCart();
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [formData, setFormData] = useState({
    fullName: '',
    address: '',
    city: '',
    postalCode: '',
    country: '',
    paymentMethod: 'Razorpay'
  });

  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const calculateTotals = () => {
    const itemsPrice = cartState.totalAmount;
    const shippingPrice = 5.00;
    const taxPrice = itemsPrice * 0.1;
    const totalPrice = itemsPrice + shippingPrice + taxPrice;

    return { itemsPrice, shippingPrice, taxPrice, totalPrice };
  };

  const loadRazorpayScript = () => {
    return new Promise((resolve) => {
      const script = document.createElement('script');
      script.src = 'https://checkout.razorpay.com/v1/checkout.js';
      script.onload = () => resolve(true);
      script.onerror = () => resolve(false);
      document.body.appendChild(script);
    });
  };

  const handleRazorpayPayment = async (orderData, orderId) => {
    const res = await loadRazorpayScript();
    
    if (!res) {
      toast.error('Razorpay SDK failed to load. Please check your connection.');
      return;
    }

    try {
      console.log('Creating Razorpay order...', { orderId, amount: orderData.totalPrice });
      
      // Create Razorpay order
      const response = await paymentAPI.createOrder({
        orderId: orderId,
        amount: orderData.totalPrice
      });

      console.log('Razorpay order created:', response.data);

      const options = {
        key: response.data.key,
        amount: response.data.amount,
        currency: response.data.currency,
        name: 'E-Shop',
        description: 'Order Payment',
        order_id: response.data.id,
        handler: async function (response) {
          try {
            console.log('Payment successful, verifying...', response);
            
            // Verify payment
            const verifyResponse = await paymentAPI.verifyPayment({
              razorpay_order_id: response.razorpay_order_id,
              razorpay_payment_id: response.razorpay_payment_id,
              razorpay_signature: response.razorpay_signature,
              orderId: orderId
            });

            console.log('Payment verification response:', verifyResponse.data);

            if (verifyResponse.data.success) {
              toast.success('ðŸŽ‰ Payment successful!');
              
              // Clear cart
              dispatch({ type: 'CLEAR_CART' });
              
              // Show detailed success messages
              setTimeout(() => {
                toast.success('âœ… Order confirmed! Your payment has been processed successfully.');
              }, 1000);
              
              setTimeout(() => {
                toast.info('ðŸšš Your order will be delivered within 10 minutes!');
              }, 2500);

              setTimeout(() => {
                toast.info('ðŸ‘¨â€ï¿½ A delivery executive will be assigned in 30 seconds!');
              }, 4000);

              setTimeout(() => {
                toast.info('ðŸ“± You will receive delivery updates via notifications!');
              }, 5500);
              
              // Redirect to order details
              setTimeout(() => {
                navigate(`/orders/${orderId}`);
              }, 7000);
            } else {
              toast.error('âŒ Payment verification failed! Please try again.');
            }
          } catch (error) {
            console.error('Payment verification error:', error);
            console.error('Error details:', {
              response: error.response?.data,
              status: error.response?.status,
              message: error.message
            });
            
            let errorMessage = 'Payment verification failed: ';
            if (error.response?.data?.message) {
              errorMessage += error.response.data.message;
            } else if (error.response?.data?.error) {
              errorMessage += error.response.data.error;
            } else if (error.message) {
              errorMessage += error.message;
            } else {
              errorMessage += 'Unknown error occurred';
            }
            
            toast.error(`âŒ ${errorMessage}`);
          }
        },
        prefill: {
          name: formData.fullName,
          email: user.email,
          contact: '9999999999'
        },
        notes: {
          address: formData.address
        },
        theme: {
          color: '#007bff'
        },
        modal: {
          ondismiss: function() {
            toast.warn('Payment cancelled. You can retry anytime.');
          }
        }
      };

      console.log('Opening Razorpay payment modal...');
      const paymentObject = new window.Razorpay(options);
      
      // Add error handler for failed payments
      paymentObject.on('payment.failed', function (response) {
        console.error('Payment failed:', response.error);
        
        let errorMessage = 'Payment failed. ';
        
        if (response.error.code === 'BAD_REQUEST_ERROR') {
          errorMessage += 'Please check your payment details and try again.';
        } else if (response.error.code === 'GATEWAY_ERROR') {
          errorMessage += 'Payment gateway error. Please try again after some time.';
        } else if (response.error.code === 'NETWORK_ERROR') {
          errorMessage += 'Network error. Please check your internet connection.';
        } else {
          errorMessage += response.error.description || 'Please try again.';
        }
        
        toast.error(`âŒ ${errorMessage}`);
        setLoading(false);
      });
      
      paymentObject.open();

    } catch (error) {
      console.error('Razorpay payment error:', error);
      toast.error(`Payment initialization failed: ${error.response?.data?.message || error.message}`);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      // Validation checks
      if (!user || !user._id) {
        toast.error('Please login to place an order');
        navigate('/login');
        return;
      }

      if (!cartState.items || cartState.items.length === 0) {
        toast.error('Your cart is empty');
        navigate('/cart');
        return;
      }

      // Validate form data
      if (!formData.fullName || !formData.address || !formData.city) {
        toast.error('Please fill all required fields');
        return;
      }

      console.log('Starting checkout process...', { 
        userId: user._id, 
        cartItems: cartState.items.length,
        user: user 
      });
      
      const { itemsPrice, shippingPrice, taxPrice, totalPrice } = calculateTotals();

      const orderData = {
        userId: user._id,
        orderItems: cartState.items.map(item => ({
          product: item.product._id,
          name: item.product.name,
          quantity: item.quantity,
          price: item.price
        })),
        shippingAddress: formData,
        paymentMethod: formData.paymentMethod,
        itemsPrice,
        taxPrice,
        shippingPrice,
        totalPrice
      };

      console.log('Creating order with data:', orderData);
      
      const response = await orderAPI.create(orderData);
      const orderId = response.data._id;
      
      console.log('Order created successfully:', orderId);
      toast.success('Order created! Proceeding to payment...');

      if (formData.paymentMethod === 'Razorpay') {
        await handleRazorpayPayment(orderData, orderId);
      } else {
        // Handle other payment methods (COD, etc.)
        dispatch({ type: 'CLEAR_CART' });
        toast.success('Order placed successfully!');
        navigate(`/orders/${orderId}`);
      }

    } catch (error) {
      console.error('Checkout error:', error);
      const errorMessage = error.response?.data?.message || error.message || 'Error creating order';
      toast.error(`Checkout failed: ${errorMessage}`);
    } finally {
      setLoading(false);
    }
  };

  if (!user) {
    return (
      <div className="checkout-page">
        <div className="container">
          <div className="text-center">
            <h2>Please login to checkout</h2>
          </div>
        </div>
      </div>
    );
  }

  if (cartState.items.length === 0) {
    return (
      <div className="checkout-page">
        <div className="container">
          <div className="text-center">
            <h2>Your cart is empty</h2>
            <button onClick={() => navigate('/products')} className="btn btn-primary">
              Continue Shopping
            </button>
          </div>
        </div>
      </div>
    );
  }

  const { itemsPrice, shippingPrice, taxPrice, totalPrice } = calculateTotals();

  return (
    <div className="checkout-page">
      <ToastContainer 
        position="top-right" 
        autoClose={5000} 
        hideProgressBar={false}
        newestOnTop={false}
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover
      />
      <div className="container">
        <h1>Checkout</h1>
        
        <div className="checkout-content">
          <div className="checkout-form">
            <form onSubmit={handleSubmit}>
              <div className="card">
                <div className="card-header">
                  <h3>ðŸšš Delivery Information</h3>
                  <p className="delivery-note">âš¡ Express delivery in 10 minutes!</p>
                </div>
                <div className="card-body">
                  <div className="form-group">
                    <label>Full Name</label>
                    <input
                      type="text"
                      name="fullName"
                      value={formData.fullName}
                      onChange={handleInputChange}
                      className="form-control"
                      required
                    />
                  </div>
                  
                  <div className="form-group">
                    <label>Address</label>
                    <input
                      type="text"
                      name="address"
                      value={formData.address}
                      onChange={handleInputChange}
                      className="form-control"
                      required
                    />
                  </div>
                  
                  <div className="form-row">
                    <div className="form-group">
                      <label>City</label>
                      <input
                        type="text"
                        name="city"
                        value={formData.city}
                        onChange={handleInputChange}
                        className="form-control"
                        required
                      />
                    </div>
                    
                    <div className="form-group">
                      <label>Postal Code</label>
                      <input
                        type="text"
                        name="postalCode"
                        value={formData.postalCode}
                        onChange={handleInputChange}
                        className="form-control"
                        required
                      />
                    </div>
                  </div>
                  
                  <div className="form-group">
                    <label>Country</label>
                    <input
                      type="text"
                      name="country"
                      value={formData.country}
                      onChange={handleInputChange}
                      className="form-control"
                      required
                    />
                  </div>
                </div>
              </div>

              <div className="card">
                <div className="card-header">
                  <h3>ðŸ’³ Payment Method</h3>
                </div>
                <div className="card-body">
                  <div className="payment-options">
                    <label className="payment-option">
                      <input
                        type="radio"
                        name="paymentMethod"
                        value="Razorpay"
                        checked={formData.paymentMethod === 'Razorpay'}
                        onChange={handleInputChange}
                      />
                      <div className="payment-info">
                        <strong>ðŸ’³ Pay with Razorpay</strong>
                        <p>Credit/Debit Card, UPI, Net Banking, Wallets</p>
                      </div>
                    </label>
                    
                    <label className="payment-option">
                      <input
                        type="radio"
                        name="paymentMethod"
                        value="Cash on Delivery"
                        checked={formData.paymentMethod === 'Cash on Delivery'}
                        onChange={handleInputChange}
                      />
                      <div className="payment-info">
                        <strong>ðŸ’µ Cash on Delivery</strong>
                        <p>Pay when your order arrives</p>
                      </div>
                    </label>
                  </div>
                </div>
              </div>

              <button 
                type="submit" 
                className="btn btn-primary place-order-btn"
                disabled={loading}
              >
                {loading ? 'Processing...' : `Place Order - â‚¹${totalPrice.toFixed(2)}`}
              </button>
            </form>
          </div>

          <div className="order-summary">
            <div className="card">
              <div className="card-header">
                <h3>ðŸ“ Order Summary</h3>
              </div>
              <div className="card-body">
                <div className="order-items">
                  {cartState.items.map(item => (
                    <div key={item.product._id} className="order-item">
                      <img src={item.product.image} alt={item.product.name} />
                      <div className="item-info">
                        <h4>{item.product.name}</h4>
                        <p>Qty: {item.quantity} Ã— â‚¹{item.price}</p>
                      </div>
                      <div className="item-total">
                        â‚¹{(item.price * item.quantity).toFixed(2)}
                      </div>
                    </div>
                  ))}
                </div>
                
                <hr />
                
                <div className="totals">
                  <div className="total-row">
                    <span>Items:</span>
                    <span>â‚¹{itemsPrice.toFixed(2)}</span>
                  </div>
                  <div className="total-row">
                    <span>Delivery:</span>
                    <span>â‚¹{shippingPrice.toFixed(2)}</span>
                  </div>
                  <div className="total-row">
                    <span>Tax (10%):</span>
                    <span>â‚¹{taxPrice.toFixed(2)}</span>
                  </div>
                  <div className="total-row final-total">
                    <span>Total:</span>
                    <span>â‚¹{totalPrice.toFixed(2)}</span>
                  </div>
                </div>

                <div className="delivery-promise">
                  <div className="promise-item">
                    <span className="icon">ðŸš€</span>
                    <span>10-minute delivery</span>
                  </div>
                  <div className="promise-item">
                    <span className="icon">ðŸ‘¨â€ðŸ³</span>
                    <span>Live order tracking</span>
                  </div>
                  <div className="promise-item">
                    <span className="icon">ðŸ“±</span>
                    <span>Real-time updates</span>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Checkout;
