import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useCart } from '../context/CartContext';
import { orderAPI, paymentAPI } from '../services/api';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import './Checkout.css';

const Checkout = () => {
  const { cartState, user, dispatch } = useCart();
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [formData, setFormData] = useState({
    fullName: '',
    address: '',
    city: '',
    postalCode: '',
    country: '',
    paymentMethod: 'Razorpay'
  });

  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const calculateTotals = () => {
    const itemsPrice = cartState.totalAmount;
    const shippingPrice = 5.00;
    const taxPrice = itemsPrice * 0.1;
    const totalPrice = itemsPrice + shippingPrice + taxPrice;

    return { itemsPrice, shippingPrice, taxPrice, totalPrice };
  };

  const loadRazorpayScript = () => {
    return new Promise((resolve) => {
      const script = document.createElement('script');
      script.src = 'https://checkout.razorpay.com/v1/checkout.js';
      script.onload = () => resolve(true);
      script.onerror = () => resolve(false);
      document.body.appendChild(script);
    });
  };

  const handleRazorpayPayment = async (orderData, orderId) => {
    const res = await loadRazorpayScript();
    
    if (!res) {
      toast.error('Razorpay SDK failed to load. Please check your connection.');
      return;
    }

    try {
      // Create Razorpay order
      const response = await paymentAPI.createOrder({
        orderId: orderId,
        amount: orderData.totalPrice
      });

      const options = {
        key: response.data.key,
        amount: response.data.amount,
        currency: response.data.currency,
        name: 'E-Shop',
        description: 'Order Payment',
        order_id: response.data.id,
        handler: async function (response) {
          try {
            // Verify payment
            const verifyResponse = await paymentAPI.verifyPayment({
              razorpay_order_id: response.razorpay_order_id,
              razorpay_payment_id: response.razorpay_payment_id,
              razorpay_signature: response.razorpay_signature,
              orderId: orderId
            });

            if (verifyResponse.data.success) {
              toast.success('Payment successful! üéâ');
              
              // Clear cart
              dispatch({ type: 'CLEAR_CART' });
              
              // Show success message with delivery info
              setTimeout(() => {
                toast.info('üöö Your order will be delivered within 10 minutes!');
              }, 2000);

              setTimeout(() => {
                toast.info('üì± A delivery partner will be assigned in 30 seconds!');
              }, 4000);
              
              // Redirect to order details
              setTimeout(() => {
                navigate(`/orders/${orderId}`);
              }, 3000);
            } else {
              toast.error('Payment verification failed!');
            }
          } catch (error) {
            toast.error('Payment verification failed!');
            console.error('Payment verification error:', error);
          }
        },
        prefill: {
          name: formData.fullName,
          email: user.email,
          contact: '9999999999'
        },
        notes: {
          address: formData.address
        },
        theme: {
          color: '#007bff'
        },
        modal: {
          ondismiss: function() {
            toast.warn('Payment cancelled. You can retry anytime.');
          }
        }
      };

      const paymentObject = new window.Razorpay(options);
      paymentObject.open();

    } catch (error) {
      toast.error('Payment initialization failed!');
      console.error('Razorpay payment error:', error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const { itemsPrice, shippingPrice, taxPrice, totalPrice } = calculateTotals();

      const orderData = {
        userId: user._id,
        orderItems: cartState.items.map(item => ({
          product: item.product._id,
          name: item.product.name,
          quantity: item.quantity,
          price: item.price
        })),
        shippingAddress: formData,
        paymentMethod: formData.paymentMethod,
        itemsPrice,
        taxPrice,
        shippingPrice,
        totalPrice
      };

      const response = await orderAPI.create(orderData);
      const orderId = response.data._id;

      if (formData.paymentMethod === 'Razorpay') {
        await handleRazorpayPayment(orderData, orderId);
      } else {
        // Handle other payment methods (COD, etc.)
        dispatch({ type: 'CLEAR_CART' });
        toast.success('Order placed successfully!');
        navigate(`/orders/${orderId}`);
      }

    } catch (error) {
      toast.error(error.response?.data?.message || 'Error creating order');
    } finally {
      setLoading(false);
    }
  };

  if (!user) {
    return (
      <div className="checkout-page">
        <div className="container">
          <div className="text-center">
            <h2>Please login to checkout</h2>
          </div>
        </div>
      </div>
    );
  }

  if (cartState.items.length === 0) {
    return (
      <div className="checkout-page">
        <div className="container">
          <div className="text-center">
            <h2>Your cart is empty</h2>
            <button onClick={() => navigate('/products')} className="btn btn-primary">
              Continue Shopping
            </button>
          </div>
        </div>
      </div>
    );
  }

  const { itemsPrice, shippingPrice, taxPrice, totalPrice } = calculateTotals();

  return (
    <div className="checkout-page">
      <ToastContainer 
        position="top-right" 
        autoClose={5000} 
        hideProgressBar={false}
        newestOnTop={false}
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover
      />
      <div className="container">
        <h1>Checkout</h1>
        
        <div className="checkout-content">
          <div className="checkout-form">
            <form onSubmit={handleSubmit}>
              <div className="card">
                <div className="card-header">
                  <h3>üöö Delivery Information</h3>
                  <p className="delivery-note">‚ö° Express delivery in 10 minutes!</p>
                </div>
                <div className="card-body">
                  <div className="form-group">
                    <label>Full Name</label>
                    <input
                      type="text"
                      name="fullName"
                      value={formData.fullName}
                      onChange={handleInputChange}
                      className="form-control"
                      required
                    />
                  </div>
                  
                  <div className="form-group">
                    <label>Address</label>
                    <input
                      type="text"
                      name="address"
                      value={formData.address}
                      onChange={handleInputChange}
                      className="form-control"
                      required
                    />
                  </div>
                  
                  <div className="form-row">
                    <div className="form-group">
                      <label>City</label>
                      <input
                        type="text"
                        name="city"
                        value={formData.city}
                        onChange={handleInputChange}
                        className="form-control"
                        required
                      />
                    </div>
                    
                    <div className="form-group">
                      <label>Postal Code</label>
                      <input
                        type="text"
                        name="postalCode"
                        value={formData.postalCode}
                        onChange={handleInputChange}
                        className="form-control"
                        required
                      />
                    </div>
                  </div>
                  
                  <div className="form-group">
                    <label>Country</label>
                    <input
                      type="text"
                      name="country"
                      value={formData.country}
                      onChange={handleInputChange}
                      className="form-control"
                      required
                    />
                  </div>
                </div>
              </div>

              <div className="card">
                <div className="card-header">
                  <h3>üí≥ Payment Method</h3>
                </div>
                <div className="card-body">
                  <div className="payment-options">
                    <label className="payment-option">
                      <input
                        type="radio"
                        name="paymentMethod"
                        value="Razorpay"
                        checked={formData.paymentMethod === 'Razorpay'}
                        onChange={handleInputChange}
                      />
                      <div className="payment-info">
                        <strong>üí≥ Pay with Razorpay</strong>
                        <p>Credit/Debit Card, UPI, Net Banking, Wallets</p>
                      </div>
                    </label>
                    
                    <label className="payment-option">
                      <input
                        type="radio"
                        name="paymentMethod"
                        value="Cash on Delivery"
                        checked={formData.paymentMethod === 'Cash on Delivery'}
                        onChange={handleInputChange}
                      />
                      <div className="payment-info">
                        <strong>üíµ Cash on Delivery</strong>
                        <p>Pay when your order arrives</p>
                      </div>
                    </label>
                  </div>
                </div>
              </div>

              <button 
                type="submit" 
                className="btn btn-primary place-order-btn"
                disabled={loading}
              >
                {loading ? 'Processing...' : `Place Order - ‚Çπ${totalPrice.toFixed(2)}`}
              </button>
            </form>
          </div>

          <div className="order-summary">
            <div className="card">
              <div className="card-header">
                <h3>üìù Order Summary</h3>
              </div>
              <div className="card-body">
                <div className="order-items">
                  {cartState.items.map(item => (
                    <div key={item.product._id} className="order-item">
                      <img src={item.product.image} alt={item.product.name} />
                      <div className="item-info">
                        <h4>{item.product.name}</h4>
                        <p>Qty: {item.quantity} √ó ‚Çπ{item.price}</p>
                      </div>
                      <div className="item-total">
                        ‚Çπ{(item.price * item.quantity).toFixed(2)}
                      </div>
                    </div>
                  ))}
                </div>
                
                <hr />
                
                <div className="totals">
                  <div className="total-row">
                    <span>Items:</span>
                    <span>‚Çπ{itemsPrice.toFixed(2)}</span>
                  </div>
                  <div className="total-row">
                    <span>Delivery:</span>
                    <span>‚Çπ{shippingPrice.toFixed(2)}</span>
                  </div>
                  <div className="total-row">
                    <span>Tax (10%):</span>
                    <span>‚Çπ{taxPrice.toFixed(2)}</span>
                  </div>
                  <div className="total-row final-total">
                    <span>Total:</span>
                    <span>‚Çπ{totalPrice.toFixed(2)}</span>
                  </div>
                </div>

                <div className="delivery-promise">
                  <div className="promise-item">
                    <span className="icon">üöÄ</span>
                    <span>10-minute delivery</span>
                  </div>
                  <div className="promise-item">
                    <span className="icon">üë®‚Äçüç≥</span>
                    <span>Live order tracking</span>
                  </div>
                  <div className="promise-item">
                    <span className="icon">üì±</span>
                    <span>Real-time updates</span>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Checkout;
