import React, { useState } from 'react';
import { useCart } from '../context/CartContext';
import { cartAPI } from '../services/api';
import { toast } from 'react-toastify';
import './ProductCard.css';

const ProductCard = ({ product }) => {
  const { user, dispatch } = useCart();
  const [loading, setLoading] = useState(false);
  const [imageError, setImageError] = useState(false);

  const handleAddToCart = async () => {
    if (!user) {
      toast.error('Please login to add items to cart');
      return;
    }

    setLoading(true);
    try {
      const response = await cartAPI.addToCart(user._id, {
        productId: product._id,
        quantity: 1
      });

      dispatch({
        type: 'SET_CART',
        payload: response.data
      });

      toast.success(`${product.name} added to cart! üõí`);
    } catch (error) {
      toast.error(error.response?.data?.message || 'Error adding to cart');
    } finally {
      setLoading(false);
    }
  };

  const handleImageError = () => {
    setImageError(true);
  };

  const formatPrice = (price) => {
    return new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR',
      minimumFractionDigits: 0
    }).format(price);
  };

  return (
    <div className="product-card">
      {product.featured && <div className="featured-badge">‚≠ê Featured</div>}
      
      <div className="product-image">
        {!imageError ? (
          <img 
            src={product.image} 
            alt={product.name}
            onError={handleImageError}
            loading="lazy"
          />
        ) : (
          <div className="image-placeholder">
            <div className="placeholder-icon">üì¶</div>
            <span>Image not available</span>
          </div>
        )}
        {product.stock === 0 && <div className="out-of-stock-overlay">Out of Stock</div>}
      </div>
      
      <div className="product-info">
        <h3 className="product-name">{product.name}</h3>
        <p className="product-description">{product.description}</p>
        <div className="product-details">
          <div className="price-section">
            <span className="product-price">{formatPrice(product.price)}</span>
            <span className="product-category">{product.category}</span>
          </div>
          <div className="stock-section">
            <span className={`product-stock ${product.stock <= 5 ? 'low-stock' : ''}`}>
              {product.stock > 0 ? (
                product.stock <= 5 ? `Only ${product.stock} left!` : `${product.stock} in stock`
              ) : 'Out of stock'}
            </span>
          </div>
        </div>
        
        <button 
          className={`btn btn-primary add-to-cart-btn ${loading ? 'loading' : ''}`}
          onClick={handleAddToCart}
          disabled={product.stock === 0 || loading}
        >
          {loading ? (
            <>
              <span className="spinner"></span>
              Adding...
            </>
          ) : product.stock === 0 ? (
            'Out of Stock'
          ) : (
            'üõí Add to Cart'
          )}
        </button>
      </div>
    </div>
  );
};

export default ProductCard;
