import React, { useState } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import { useCart } from '../context/CartContext';
import Logo from './Logo';
import './Header.css';

const Header = () => {
  const { cartState, user, setUser } = useCart();
  const navigate = useNavigate();
  const location = useLocation();
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);

  const handleLogout = () => {
    setUser(null);
    navigate('/');
    setMobileMenuOpen(false);
  };

  const cartItemCount = cartState.items.reduce((total, item) => total + item.quantity, 0);

  const isActive = (path) => {
    return location.pathname === path;
  };

  return (
    <header className="header">
      <div className="container">
        <div className="header-content">
          <Link to="/" className="logo" onClick={() => setMobileMenuOpen(false)}>
            <div className="logo-icon">
              <svg width="40" height="40" viewBox="0 0 40 40" fill="none" xmlns="http://www.w3.org/2000/svg">
                <circle cx="20" cy="20" r="18" fill="url(#gradient)" stroke="white" strokeWidth="2"/>
                <path d="M12 16l8 6 8-6" stroke="white" strokeWidth="2.5" strokeLinecap="round" strokeLinejoin="round"/>
                <path d="M12 22l8 6 8-6" stroke="white" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                <defs>
                  <linearGradient id="gradient" x1="0%" y1="0%" x2="100%" y2="100%">
                    <stop offset="0%" stopColor="#4facfe"/>
                    <stop offset="100%" stopColor="#00f2fe"/>
                  </linearGradient>
                </defs>
              </svg>
            </div>
            <div className="logo-text">
              <h1>Grab It</h1>
              <span className="logo-tagline">Quick & Easy Shopping</span>
            </div>
          </Link>
          
          <nav className={`nav ${mobileMenuOpen ? 'nav-open' : ''}`}>
            <Link 
              to="/" 
              className={`nav-link ${isActive('/') ? 'active' : ''}`}
              onClick={() => setMobileMenuOpen(false)}
            >
              üè† Home
            </Link>
            <Link 
              to="/products" 
              className={`nav-link ${isActive('/products') ? 'active' : ''}`}
              onClick={() => setMobileMenuOpen(false)}
            >
              üì¶ Products
            </Link>
            {user && (
              <Link 
                to="/orders" 
                className={`nav-link ${isActive('/orders') ? 'active' : ''}`}
                onClick={() => setMobileMenuOpen(false)}
              >
                üìã My Orders
              </Link>
            )}
          </nav>

          <div className="header-actions">
            <Link 
              to="/cart" 
              className="cart-link"
              onClick={() => setMobileMenuOpen(false)}
            >
              <div className="cart-icon-container">
                <span className="cart-icon">üõí</span>
                {cartItemCount > 0 && (
                  <span className="cart-count">{cartItemCount}</span>
                )}
              </div>
              <span className="cart-text">Cart</span>
            </Link>
            
            {user ? (
              <div className="user-menu">
                <div className="user-avatar">
                  <span className="avatar-icon">üë§</span>
                </div>
                <div className="user-info">
                  <span className="user-greeting">Hello,</span>
                  <span className="user-name">{user.name}</span>
                </div>
                <button 
                  onClick={handleLogout} 
                  className="btn btn-logout"
                  title="Logout"
                >
                  üö™
                </button>
              </div>
            ) : (
              <div className="auth-links">
                <Link 
                  to="/login" 
                  className="btn btn-primary"
                  onClick={() => setMobileMenuOpen(false)}
                >
                  Login
                </Link>
                <Link 
                  to="/register" 
                  className="btn btn-secondary"
                  onClick={() => setMobileMenuOpen(false)}
                >
                  Register
                </Link>
              </div>
            )}
            
            <button 
              className="mobile-menu-toggle"
              onClick={() => setMobileMenuOpen(!mobileMenuOpen)}
              aria-label="Toggle menu"
            >
              <span></span>
              <span></span>
              <span></span>
            </button>
          </div>
        </div>
      </div>
    </header>
  );
};

export default Header;
